import axios from 'axios';

// ACTION TYPES;
const FETCH_ALL_CAMPUSES = "FETCH_ALL_CAMPUSES";

// ACTION CREATORS;
const fetchAllCampuses = campuses => {
  return {
    type: FETCH_ALL_CAMPUSES,
    payload: campuses.campuses
  }
}

// THUNK CREATORS;
export const fetchAllCampusesThunk = () => dispatch => {
  return axios
    .get('http://localhost:8081/api/campuses')
    .then(res => res.data)
    .then(campuses => dispatch(fetchAllCampuses(campuses)))
    .catch(err => console.log(err))
}

// return {
//   ...state, allCampuses: [action.payload]
// };

let defaultState = {
  allCampuses: []
}
// REDUCER;
const reducer = (state = defaultState, action) => {
  switch (action.type) {
    case FETCH_ALL_CAMPUSES:
      return  {
        ...state, allCampuses: action.payload 
      };
    default:
      return state;
  }
}

export default reducer;
