import React from 'react';
import PropTypes from 'prop-types';
import './styles/allCampusesView.css';
import { useHistory } from 'react-router-dom'

const history = useHistory();

const AllCampusesView = props => {
  return (
    <div className="grid">
      {props.fetchAllCampuses}
      {props.allCampuses.map(campus => (
        <div key={campus.id}>
          <img className="defualt-icon" src={campus.img} onClick={() => { history.push(`/campuses/${campus.id}`) }} />
          <h3 onClick={() => { history.push(`/campuses/${campus.id}`) }}>{campus.name}</h3>
          Students enrolled: {campus.hasOwnProperty("students") === false ? 0 : campus.students.length } <br />
          <button onClick={() => {props.deleteCampus(campus.id)}}> delete </button>
          <button onClick={() => { history.push(`/campuses/${campus.id}/edit`) }}>edit</button>
        </div>
      ))}
    </div>
  );
};

AllCampusesView.propTypes = {
  allCampuses: PropTypes.array.isRequired
};

export default AllCampusesView;
