import React, { Component } from 'react';
import { connect } from 'react-redux';
// import PropTypes from 'prop-types';
// import { connect } from 'react-redux';
// import { fetchAllCampusesThunk, deleteCampus, addCampus } from '../../thunks';
// import { AllCampusesView } from '../views';


class SingleCampusContainerEdit extends Component {
  constructor(props){
    super(props)
    this.state = {
      campusName: "",
      campusAddress: "",
      campusImgURL: "",
      campusInfo: ""
    }
  }

  handleChange = (event) => { this.setState({ [event.target.name]: event.target.value }) }

  render(){
    return (
      <div>
        <form onSubmit={this.handleSubmit} class="user-input">
          <label> 
            Campus Name: <input name="campusName" type="text" value={this.state.campusName} onChange={this.handleChange} required/> 
          </label>
          <label>
            Address: <input name="campusAddress" type="text" value={this.state.campusAddress} onChange={this.handleChange} required/> 
          </label>  
          <label>
            Address: <input name="campusAddress" type="text" value={this.state.campusAddress} onChange={this.handleChange} required/> 
          </label>  
          <input class="button" type="submit" value="Add Campus" />
        </form>
      </div>
    )
  }
}

// Map state to props;
const mapState = state => {
  return {
    campus: state.allCampuses
  }
}

// Map dispatch to props;
const mapDispatch = dispatch => {
  return {
    // fetchAllCampuses: () => dispatch(fetchAllCampusesThunk()),
    // deleteCampus: (campusID) => dispatch(deleteCampus(campusID)),
    // addCampus:(campus) => dispatch(addCampus(campus))
  }
}

// Type check props;
// AllCampusesContainer.propTypes = {
//   allCampuses: PropTypes.array.isRequired,
//   fetchAllCampuses: PropTypes.func.isRequired
// }

// Export our store-connected container by default;
export default connect(mapState, mapDispatch)(SingleCampusContainerEdit);
