// Barrel file for thunks, which will be mapped to and invoked within our smart containers;
export * from '../store/utilities/allPlayers';
export * from '../store/utilities/allCampuses';

// THUNK CREATORS;
export const fetchAllCampusesThunk = () => dispatch => {
  return axios
    .get('/api/campuses')
    .then(res => res.data)
    .then(campuses => dispatch(fetchAllCampuses(campuses)))
    .catch(err => console.log(err))
}
