import React, { Component } from 'react';
import { AllCampusesView } from '../views';
import { connect } from 'react-redux';
import { fetchAllCampuses, deleteCampus, addCampus } from '../../thunks';
import { NavBarView } from '../views';
import './styles/allCampusesView.css';
class SingleCampusContainer extends Component {

  componentDidMount () {
    while(true) {
      fetchAllCampuses()
      if (this.props.campus !== undefined)
        break
    }
  }
  render(){
    return (
      <div>
        <NavBarView />
        <h1>{this.props.campus.name}</h1>
        <img src={this.props.campus.img} />
        {this.props.campusinfo}
        {this.props.campusaddress}
        {props.campus.student.map(campus => (
        <div key={campus.id}>
          <img className="defualt-icon" src={campus.img} onClick={() => { props.history.push(`/campuses/${campus.id}`) }} />
          <h3 onClick={() => { props.history.push(`/campuses/${campus.id}`) }}>{campus.name}</h3>
          {console.log("HERE", campus)}
          Students enrolled: {campus.hasOwnProperty("students") === false ? 0 : campus.students.length } <br />
          <button onClick={() => {props.deleteCampus(campus.id)}}> delete </button>
          <button onClick={() => { props.history.push(`/campuses/${campus.id}/edit`) }}>edit</button>
        </div>
      ))}
      </div>
    )}
}


const mapState = (state, ownprops) => {
  return {
    campus: state.allCampuses.find(campus => campus.id == ownprops.match.params.id), 
  }
}
const mapDispatch = dispatch => {
  return {
    fetchAllCampuses: () => dispatch(fetchAllCampuses()),
  }
}

// Export our store-connected container by default;
export default connect(mapState, mapDispatch)(SingleCampusContainer);
